export const Patient = ({patient}) => {
    return(
        <div className="flex h-screen justify-center items-center">
            <div className="fixed top-4 left-4"><button>Log Out</button></div>
            <div className='bg-white w-1/3 shadow-xl rounded-lg p-6 mb-4' key={patient.id}>
                            <h1 className='text-lg font-semibold capitalize'>Patient: {patient.name}</h1>
                            <ul className='mt-4 space-y-2'>
                                {patient.medicines.map(medicine => (
                                    <li key={medicine.name} className='border-t pt-2'>
                                        <h2 className='text-md font-medium capitalize'>Medicine: {medicine.name}</h2>
                                        <h4>Interval: {medicine.interval} hours</h4>
                                        <h4>Dosage: {medicine.dosage}</h4>
                                        <h4>Next Time: {medicine.nextDosageTimeFormatted}</h4>
                                    </li>
                                ))}
                            </ul>
            </div>
        </div>
    )
}
