import React, { useEffect, useState, useRef } from 'react';

const Alerts = () => {
  const [alerts, setAlerts] = useState([]);
  const alertTimeouts = useRef({}); // Ref to store timeout IDs

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:3001'); // Adjust the URL if needed

    ws.onmessage = (event) => {
      const { alert } = JSON.parse(event.data);

      setAlerts(prevAlerts => {
        // Remove old timeout for this alert if exists
        if (alertTimeouts.current[alert.patient.id] && alertTimeouts.current[alert.patient.id][alert.medicine.name]) {
          clearTimeout(alertTimeouts.current[alert.patient.id][alert.medicine.name]);
        }

        // Add new alert or update existing one
        const updatedAlerts = prevAlerts.filter(existingAlert => 
          !(existingAlert.patient.id === alert.patient.id && existingAlert.medicine.name === alert.medicine.name)
        );
        const newAlerts = [...updatedAlerts, alert];

        // Set timeout to remove alert if no new alert is received in the next 30 seconds
        if (!alertTimeouts.current[alert.patient.id]) {
          alertTimeouts.current[alert.patient.id] = {};
        }
        alertTimeouts.current[alert.patient.id][alert.medicine.name] = setTimeout(() => {
          setAlerts(prevAlerts => prevAlerts.filter(existingAlert => 
            !(existingAlert.patient.id === alert.patient.id && existingAlert.medicine.name === alert.medicine.name)
          ));
        }, 30000); // 30 seconds timeout

        return newAlerts;
      });
    };

    return () => {
      ws.close();
      // Clean up timeouts on component unmount
      Object.values(alertTimeouts.current).forEach(timeouts => 
        Object.values(timeouts).forEach(timeout => clearTimeout(timeout))
      );
    };
  }, []);

  return (
    <div className='fixed right-10 top-10 overflow-scroll'>
      <ul>
        {alerts.map((alert, index) => (
          <li key={index} className='mb-2 p-4 border border-gray-200 rounded shadow'>
            <div><strong>Patient:</strong> {alert.patient.name} (ID: {alert.patient.id})</div>
            <div><strong>Medicine:</strong> {alert.medicine.name}</div>
            <div><strong>Dosage:</strong> {alert.medicine.dosage}</div>
            <div><strong>Interval:</strong> {alert.medicine.interval}</div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Alerts;
