import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { CountdownTimer } from '../components/CountdownTimer';
import Alerts from '../components/Alert';
import FeatherIcon from 'feather-icons-react';

export const Doctor = ({ doctorName }) => {
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState('');
    const [editingUserId, setEditingUserId] = useState(null);
    const [editFormData, setEditFormData] = useState({});

    useEffect(() => {
        getUsers(); // Fetch users initially when the component mounts
        const interval = setInterval(() => {
            getUsers(); // Setup interval to fetch users every second
        }, 1000);

        return () => clearInterval(interval);
    }, []);

    const getUsers = async () => {
        try {
            const response = await axios.get('http://localhost:3001/users');
            setUsers(response.data);
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    const handleEditClick = (user) => {
        if (editingUserId === user.id) {
            // If the same user is clicked again, cancel editing
            setEditingUserId(null);
            setEditFormData({});
        } else {
            // Start editing the clicked user
            setEditingUserId(user.id);
            setEditFormData({
                medicines: user.medicines.reduce((acc, medicine) => {
                    acc[medicine.name] = {
                        interval: medicine.interval,
                        dosage: medicine.dosage,
                        name: medicine.name
                    };
                    return acc;
                }, {})
            });
        }
    };

    const handleEditChange = (medicineName, field, value) => {
        setEditFormData(prev => ({
            ...prev,
            medicines: {
                ...prev.medicines,
                [medicineName]: {
                    ...prev.medicines[medicineName],
                    [field]: value
                }
            }
        }));
    };

    const handleCheckboxChange = (medicineName) => {
        setEditFormData(prev => ({
            ...prev,
            medicines: {
                ...prev.medicines,
                [medicineName]: prev.medicines[medicineName] ? undefined : { interval: '', dosage: '', name: medicineName }
            }
        }));
    };

    const handleSaveChanges = async () => {
        try {
            const updatedMedicines = Object.values(editFormData.medicines).filter(Boolean);
            await axios.put(`http://localhost:3001/users/${editingUserId}`, { medicines: updatedMedicines });
            setEditingUserId(null);
            setEditFormData({});
            getUsers();
            setMessage('User updated successfully');
        } catch (error) {
            setMessage(error.response.data.message);
        }
    };

    return (
        <div className='w-4/5 ml-auto pl-10 pt-8'>
            <h1 className='text-3xl mb-4'>Patients</h1>

            <div>
                <h2 className='text-red-500'>{message}</h2>
            </div>

            <div className='space-y-4'>
                {users
                    .filter(user => user.name !== doctorName)
                    .map(user => (
                        <div className='bg-white w-11/12 shadow-lg rounded-lg p-6 mb-4' key={user.id}>
                            <div className='flex flex-row justify-between'>
                                <h1 className='text-lg font-semibold capitalize'>Patient: {user.name} (ID: {user.id})</h1>
                                <button onClick={() => handleEditClick(user)} className='mr-1'>
                                    <FeatherIcon icon='edit' size={18} />
                                </button>
                            </div>

                            {editingUserId === user.id ? (
                                <div>
                                    <h2 className='text-md font-medium mt-4'>Edit Medicines</h2>
                                    {['Advil', 'Tylenol'].map(medicineName => (
                                        <div key={medicineName} className='mb-4'>
                                            <label>
                                                <input
                                                    type="checkbox"

                                                    checked={!!editFormData.medicines[medicineName]}
                                                    onChange={() => handleCheckboxChange(medicineName)}
                                                />
                                                {medicineName}
                                            </label>
                                            {editFormData.medicines[medicineName] && (
                                                <div className='mt-2'>
                                                    <div className='mb-2'>
                                                        <label>Interval (hours):</label>
                                                        <input
                                                            className='bg-gray-200 px-2 ml-2 py-1 rounded-md'
                                                            type="number"
                                                            value={editFormData.medicines[medicineName].interval}
                                                            onChange={(e) => handleEditChange(medicineName, 'interval', e.target.value)}
                                                        />
                                                    </div>
                                                    <div className='mb-2'>
                                                        <label>Dosage:</label>
                                                        <input
                                                            type="number"
                                                            value={editFormData.medicines[medicineName].dosage}
                                                            onChange={(e) => handleEditChange(medicineName, 'dosage', e.target.value)}
                                                        />
                                                    </div>
                                                </div>
                                            )}
                                        </div>
                                    ))}
                                    <button onClick={handleSaveChanges} className='mt-2'>Save Changes</button>
                                </div>
                            ) : (
                                <ul className='mt-4 space-y-2'>
                                    {user.medicines.map(medicine => (
                                        <li key={medicine.name} className='border-t pt-2'>
                                            <h2 className='text-md font-medium capitalize'>Medicine: {medicine.name}</h2>
                                            <h4>Interval: {medicine.interval} hours</h4>
                                            <h4>Dosage: {medicine.dosage}</h4>
                                            <h4>Next Dosage Time: {medicine.nextDosageTimeFormatted}</h4>
                                            <CountdownTimer targetTime={medicine.nextDosageTime} />
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </div>
                    ))}
            </div>
        </div>
    );
};
