import { Navigate } from "react-router-dom";
import FeatherIcon from "feather-icons-react";
import fingerprintImage from '../images/fingersensor.gif';
import logo from '../images/pharmaboticsLogo.png';

export const Login = ({ loggedIn, checkFingerprint, isDoctor, name, message }) => {
    return (
        <div>
            {loggedIn ? (
                isDoctor ? (
                    <Navigate to="/doctor" />
                ) : (
                    <Navigate to="/patient" />
                )
            ) : (
                <div className="w-screen flex-col gap-4 h-screen flex items-center bgColor justify-center">
                    <img className="fixed bottom-10 w-40" src={logo}></img>
                    <p className="fixed bottom-4 textColor text-xs">Pharmaboticsâ„¢ 2024</p>
                    <img className="w-1/2 h-auto" src={fingerprintImage}></img>
                    <button className='rounded-xl fixed flex flex-row items-center gap-2 text-lg mt-72 tracking-wide text-sm px-5 py-3 w-max logInBtn' onClick={checkFingerprint}><FeatherIcon icon='log-in' size={15}/> Scan Fingerprint</button>
                    <div className="mt-32 quicksand-regular w-1/4 text-center">{message}</div>
                </div>
            )}
        </div>
    );
};
