import React, { useEffect, useState } from 'react';

const Alerts = () => {
  const [alerts, setAlerts] = useState([]);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:3001'); // Adjust the URL if needed

    ws.onmessage = (event) => {
      const { alert } = JSON.parse(event.data);

      // Check for duplicates based on patient.id
      if (!alerts.some(existingAlert => existingAlert.id === alert.id)) {
        setAlerts(prevAlerts => [...prevAlerts, alert]);
      }
    };

    return () => {
      ws.close();
    };
  }, [alerts]); // Include alerts in dependency array to handle updates

  return (
    <div className='fixed right-10 top-10'>
      <ul>
        {alerts.map((alert, index) => (
          <li key={index}>
            {`Time for ${alert.name} (${alert.id}) to take ${alert.medicine.dosage} ${alert.medicine.name} at ${new Date(alert.medicine.nextDosageTime).toLocaleTimeString()}`}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Alerts;
