import FeatherIcon from "feather-icons-react";

export const Patient = ({ patient, onLogout }) => {
  const checkFingerprint = async () => {
    try {
      const response = await axios.post("http://localhost:3002/check", {
        id: userId,
      });

      console.log(response.data);

      if (response.data.isDoctor) {
        setLoggedIn(true);
        setIsDoctor(true);
        setDoctorName(response.data.name);
      } else {
        setLoggedIn(true);
        setIsDoctor(false);
        setName(response.data.name);
        setPatient(response.data);
      }
      setMessage(`Fingerprint matched for user: ${response.data.name}`); // Access 'name' property from response data
    } catch (error) {
      if (error.response && error.response.status === 404) {
        setMessage(
          "No fingerprint match found. You are either not a registered patient, or please try to scan again."
        );
      } else {
        setMessage(
          error.response?.data?.message || "Error checking fingerprint"
        ); // Display the error message from the server
      }
    }
  };

  return (
    <div className="flex h-screen justify-center items-center">
      <div className="fixed top-4 left-4">
        <button
          className="rounded-xl flex flex-row items-center gap-2 text-lg tracking-wide text-xs px-5 py-3 w-max logInBtn"
          onClick={onLogout}
        >
          <FeatherIcon icon="log-out" size={15} /> Log Out
        </button>
      </div>
      {patient ? (
        <div
          className="bg-white w-1/3 shadow-xl rounded-lg p-6 mb-4"
          key={patient.id}
        >
          <h1 className="text-lg font-semibold capitalize">
            Patient: {patient.name}
          </h1>
          <ul className="mt-4 space-y-2">
            {patient.medicines?.map((medicine) => (
              <li key={medicine.name} className="border-t pt-2">
                <h2 className="text-md font-medium capitalize">
                  Medicine: {medicine.name}
                </h2>
                <h4>Interval: {medicine.interval} hours</h4>
                <h4>Dosage: {medicine.dosage}</h4>
                <h4>Next Time: {medicine.nextDosageTimeFormatted}</h4>
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <div className="bg-white w-1/3 shadow-xl rounded-lg p-6 mb-4">
          <h1 className="text-lg font-semibold">No patient data available</h1>
        </div>
      )}
    </div>
  );
};
